import * as React from "react";
import { Form, Button } from "semantic-ui-react";
import Auth from "../auth/Auth";
import {
  getUploadUrl,
  uploadFile,
  patchTodoAttachment,
} from "../api/todos-api";

enum UploadState {
  NoUpload,
  FetchingPresignedUrl,
  UploadingFile,
}

interface EditTodoProps {
  match: {
    params: {
      todoId: string;
    };
  };
  auth: Auth;
}

interface EditTodoState {
  file: any;
  uploadState: UploadState;
  filename: string;
}

export class EditTodo extends React.PureComponent<
  EditTodoProps,
  EditTodoState
> {
  state: EditTodoState = {
    file: undefined,
    filename: "",
    uploadState: UploadState.NoUpload,
  };

  handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = event.target.files;
    if (!files) return;

    this.setState({
      file: files[0],
      filename: files[0].name,
    });
  };

  handleSubmit = async (event: React.SyntheticEvent) => {
    event.preventDefault();

    try {
      if (!this.state.file) {
        alert("File should be selected");
        return;
      }

      this.setUploadState(UploadState.FetchingPresignedUrl);
      const uploadUrl = await getUploadUrl(
        this.props.auth.getIdToken(),
        `${this.props.match.params.todoId}/${this.state.filename}`
      );

      this.setUploadState(UploadState.UploadingFile);
      await uploadFile(uploadUrl, this.state.file);
      await patchTodoAttachment(
        this.props.auth.getIdToken(),
        this.props.match.params.todoId,
        `${this.props.match.params.todoId}/${this.state.filename}`
      );

      alert("File was uploaded!");
    } catch (e) {
      alert("Could not upload a file: " + e);
    } finally {
      this.setUploadState(UploadState.NoUpload);
    }
  };

  setUploadState(uploadState: UploadState) {
    this.setState({
      uploadState,
    });
  }

  render() {
    return (
      <div>
        <h1>Upload new image</h1>

        <Form onSubmit={this.handleSubmit}>
          <Form.Field>
            <label>File</label>
            <input
              type="file"
              accept="image/*"
              placeholder="Image to upload"
              onChange={this.handleFileChange}
              disabled={this.state.uploadState === UploadState.FetchingPresignedUrl || this.state.uploadState === UploadState.UploadingFile}
            />
          </Form.Field>

          {this.renderButton()}
        </Form>
      </div>
    );
  }

  renderButton() {
    return (
      <div>
        {this.state.uploadState === UploadState.FetchingPresignedUrl && (
          <p>Uploading image metadata</p>
        )}
        {this.state.uploadState === UploadState.UploadingFile && (
          <p>Uploading file</p>
        )}
        <Button
          loading={this.state.uploadState !== UploadState.NoUpload}
          type="submit"
        >
          Upload
        </Button>
      </div>
    );
  }
}
